// set seed (counters) normal + uniform
void sseed(int i) {
	gnumber_counter = i;
	unumber_counter = i;
}

// get seed (counter) normal
int gseedn() {
	return gnumber_counter;
}

// get seed (counter) uniform
int gseedu() {
	return unumber_counter;
}

// random uniform in [0,1]
double randu() {
	return unumbers[++unumber_counter % BUF_NUMBERS];
}

// random uniform in [a,b]
double randu_from_range(double a, double b) {
	return a + (randu()*(b-a));
}

// random gaussian
double randn() {
	//gnumber_counter = (++gnumber_counter % BUF_NUMBERS);
	return gnumbers[++gnumber_counter % BUF_NUMBERS];
	//return gnumbers[(int)rand() % BUF_NUMBERS];
}

void vec_set_randn(double v[], int N) {
	int i;
	for (i = 0; i < N; i++) {
		v[i] = randn();
	}
}

void vec_set_rand(double v[], int N) {
	int i;
	for (i = 0; i < N; i++) {
		v[i] = randu();
	}
}

